import React, { useState } from 'react';

interface ExpiryProduct {
  id: number;
  name: string;
  expiryDate: Date;
  category: string;
  quantity: number;
  image: string;
}

const categories = ['전체', '채소', '과일', '육류', '유제품'];

export const CalendarPage: React.FC = () => {
  const [selectedDate, setSelectedDate] = useState<Date>(new Date());
  const [selectedCategory, setSelectedCategory] = useState<string>('');

  // 임시 데이터 (실제로는 API에서 가져와야 함)
  const [products] = useState<ExpiryProduct[]>([
    {
      id: 1,
      name: '신선한 당근',
      expiryDate: new Date(2024, 3, 15),
      category: '채소',
      quantity: 5,
      image: '/images/carrot.jpg'
    },
    // 더 많은 상품 데이터 추가 가능
  ]);

  // 달력 생성 함수
  const generateCalendar = () => {
    const year = selectedDate.getFullYear();
    const month = selectedDate.getMonth();
    const firstDay = new Date(year, month, 1);
    const lastDay = new Date(year, month + 1, 0);
    const daysInMonth = lastDay.getDate();
    const startingDay = firstDay.getDay();

    const calendar = [];
    let day = 1;

    // 달력 헤더 (요일)
    const weekdays = ['일', '월', '화', '수', '목', '금', '토'];
    calendar.push(
      <div key="weekdays" className="grid grid-cols-7 gap-1 mb-2">
        {weekdays.map(weekday => (
          <div key={weekday} className="text-center font-semibold text-gray-600">
            {weekday}
          </div>
        ))}
      </div>
    );

    // 달력 날짜
    for (let i = 0; i < 6; i++) {
      const week = [];
      for (let j = 0; j < 7; j++) {
        if (i === 0 && j < startingDay) {
          week.push(<div key={`empty-${j}`} className="h-24"></div>);
        } else if (day > daysInMonth) {
          week.push(<div key={`empty-end-${j}`} className="h-24"></div>);
        } else {
          const currentDate = new Date(year, month, day);
          const dayProducts = products.filter(product => {
            const productDate = new Date(product.expiryDate);
            return productDate.getDate() === day &&
                   productDate.getMonth() === month &&
                   productDate.getFullYear() === year &&
                   (!selectedCategory || product.category === selectedCategory);
          });

          week.push(
            <div
              key={day}
              className={`h-24 p-1 border rounded-lg ${
                dayProducts.length > 0 ? 'bg-red-50' : ''
              }`}
            >
              <div className="text-sm font-medium mb-1">{day}</div>
              {dayProducts.map(product => (
                <div
                  key={product.id}
                  className="text-xs p-1 bg-white rounded shadow-sm mb-1 truncate"
                >
                  {product.name} ({product.quantity}개)
                </div>
              ))}
            </div>
          );
          day++;
        }
      }
      calendar.push(
        <div key={`week-${i}`} className="grid grid-cols-7 gap-1">
          {week}
        </div>
      );
    }

    return calendar;
  };

  // 이전 달로 이동
  const prevMonth = () => {
    setSelectedDate(new Date(selectedDate.getFullYear(), selectedDate.getMonth() - 1));
  };

  // 다음 달로 이동
  const nextMonth = () => {
    setSelectedDate(new Date(selectedDate.getFullYear(), selectedDate.getMonth() + 1));
  };

  return (
    <div className="min-h-screen bg-[#F8FAF7] py-8">
      <div className="max-w-7xl mx-auto px-4">
        <h1 className="text-2xl font-bold text-gray-900 mb-6">유통기한 달력</h1>

        {/* 필터 섹션 */}
        <div className="bg-white rounded-lg shadow-sm p-6 mb-6">
          <div className="flex items-center justify-between mb-4">
            <div className="flex items-center gap-4">
              <button
                onClick={prevMonth}
                className="p-2 rounded-lg hover:bg-gray-100"
              >
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M15 19l-7-7 7-7" />
                </svg>
              </button>
              <h2 className="text-xl font-semibold">
                {selectedDate.getFullYear()}년 {selectedDate.getMonth() + 1}월
              </h2>
              <button
                onClick={nextMonth}
                className="p-2 rounded-lg hover:bg-gray-100"
              >
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 5l7 7-7 7" />
                </svg>
              </button>
            </div>

            <select
              value={selectedCategory}
              onChange={(e) => setSelectedCategory(e.target.value)}
              className="px-4 py-2 rounded-lg border border-gray-300 focus:border-[#4CAF50] focus:ring-1 focus:ring-[#4CAF50] focus:outline-none"
            >
              <option value="">전체 카테고리</option>
              {categories.map(category => (
                <option key={category} value={category}>{category}</option>
              ))}
            </select>
          </div>

          {/* 달력 */}
          <div className="bg-white rounded-lg">
            {generateCalendar()}
          </div>
        </div>

        {/* 범례 */}
        <div className="bg-white rounded-lg shadow-sm p-4">
          <h3 className="text-sm font-medium text-gray-700 mb-2">범례</h3>
          <div className="flex items-center gap-4">
            <div className="flex items-center gap-2">
              <div className="w-4 h-4 bg-red-50 rounded"></div>
              <span className="text-sm text-gray-600">유통기한 임박 상품</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
