import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

interface AddProductForm {
  name: string;
  description: string;
  category: string;
  addedDate: string;
  expiryDate: string;
}

const categories = ['Ï±ÑÏÜå', 'Í≥ºÏùº', 'Ïú°Î•ò', 'Ïú†Ï†úÌíà'];

export const AddPage: React.FC = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const editingProduct = location.state?.product; // ÏàòÏ†ïÎ™®ÎìúÏùº ÎïåÎßå Í∞í ÏûàÏùå

  // Î°úÍ∑∏Ïù∏ Ïú†Ï†Ä Ï†ïÎ≥¥
  const currentUser = localStorage.getItem('currentUser')
    ? JSON.parse(localStorage.getItem('currentUser')!)
    : null;

  // Ìèº ÏÉÅÌÉú (ÏàòÏ†ïÎ™®Îìú: productÍ∞í, Îì±Î°ùÎ™®Îìú: ÎπàÍ∞í)
  const [form, setForm] = useState<AddProductForm>({
    name: editingProduct?.name ?? '',
    description: editingProduct?.description ?? '',
    category: editingProduct?.category ?? categories[0],
    addedDate: editingProduct?.addedDate ?? '',
    expiryDate: editingProduct?.expiryDate ?? ''
  });

  // location.stateÍ∞Ä Î∞îÎÄåÎ©¥(ÏàòÏ†ï ÏßÑÏûÖ) Ìèº Ï¥àÍ∏∞Ìôî
  useEffect(() => {
    if (editingProduct) {
      setForm({
        name: editingProduct.name,
        description: editingProduct.description,
        category: editingProduct.category,
        addedDate: editingProduct.addedDate,
        expiryDate: editingProduct.expiryDate
      });
    }
  }, [editingProduct]);

  // ÏûÖÎ†• Ìï∏Îì§Îü¨
  const handleInputChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>
  ) => {
    const { name, value } = e.target;
    setForm(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Ï†úÏ∂ú
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!currentUser || !currentUser.id) {
      alert('Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.');
      return;
    }

    try {
      let res;
      if (editingProduct) {
        // **ÏàòÏ†ïÎ™®Îìú: PUT**
        res = await fetch(
          `http://localhost:8080/api/ingredients/${editingProduct.id}`,
          {
            method: 'PUT',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(form),
          }
        );
      } else {
        // **Îì±Î°ùÎ™®Îìú: POST**
        res = await fetch(
          `http://localhost:8080/api/ingredients/add/${currentUser.id}`,
          {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(form),
          }
        );
      }

      if (res.ok) {
        alert(editingProduct ? 'ÏàòÏ†ï ÏÑ±Í≥µ!' : 'ÏãùÏû¨Î£å Îì±Î°ù ÏÑ±Í≥µ!');
        navigate('/');
      } else {
        const errorText = await res.text();
        alert((editingProduct ? 'ÏàòÏ†ï Ïã§Ìå®: ' : 'Îì±Î°ù Ïã§Ìå®: ') + errorText);
      }
    } catch (error) {
      console.error('ÏÑúÎ≤Ñ Ïò§Î•ò Î∞úÏÉù:', error);
      alert('ÏÑúÎ≤ÑÏôÄÏùò ÌÜµÏã† Ï§ë Î¨∏Ï†úÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
    }
  };

  return (
    <div className="min-h-screen bg-[rgb(118,188,130)] py-8">
      <div className="max-w-2xl mx-auto bg-white rounded-lg shadow-sm p-6">
        <h1 className="text-2xl font-semibold text-gray-800 mb-6 text-center font-['Pretendard',sans-serif] tracking-tight">
          {editingProduct ? '‚úèÔ∏è ÏãùÏû¨Î£å ÏàòÏ†ï' : 'üìù ÏãùÏû¨Î£å Îì±Î°ù'}
        </h1>
        <form onSubmit={handleSubmit} className="space-y-6">
          <div>
            <label htmlFor="name" className="block text-sm font-medium text-gray-700 mb-2">
              ÏãùÏû¨Î£åÎ™Ö
            </label>
            <input
              type="text"
              id="name"
              name="name"
              value={form.name}
              onChange={handleInputChange}
              required
              className="w-full px-4 py-2 rounded-lg border border-gray-300 focus:border-[#4CAF50] focus:ring-1 focus:ring-[#4CAF50] focus:outline-none"
              placeholder="ÏãùÏû¨Î£åÎ™ÖÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
            />
          </div>
          <div>
            <label htmlFor="category" className="block text-sm font-medium text-gray-700 mb-2">
              Ïπ¥ÌÖåÍ≥†Î¶¨
            </label>
            <select
              id="category"
              name="category"
              value={form.category}
              onChange={handleInputChange}
              className="w-full px-4 py-2 rounded-lg border border-gray-300 focus:border-[#4CAF50] focus:ring-1 focus:ring-[#4CAF50] focus:outline-none"
            >
              {categories.map(category => (
                <option key={category} value={category}>{category}</option>
              ))}
            </select>
          </div>
          <div>
            <label htmlFor="addedDate" className="block text-sm font-medium text-gray-700 mb-2">
              Îì±Î°ùÏùº
            </label>
            <input
              type="date"
              id="addedDate"
              name="addedDate"
              value={form.addedDate}
              onChange={handleInputChange}
              required
              className="w-full px-4 py-2 rounded-lg border border-gray-300 focus:border-[#4CAF50] focus:ring-1 focus:ring-[#4CAF50] focus:outline-none"
            />
          </div>
          <div>
            <label htmlFor="expiryDate" className="block text-sm font-medium text-gray-700 mb-2">
              Ïú†ÌÜµÍ∏∞Ìïú
            </label>
            <input
              type="date"
              id="expiryDate"
              name="expiryDate"
              value={form.expiryDate}
              onChange={handleInputChange}
              required
              className="w-full px-4 py-2 rounded-lg border border-gray-300 focus:border-[#4CAF50] focus:ring-1 focus:ring-[#4CAF50] focus:outline-none"
            />
          </div>
          <div>
            <label htmlFor="description" className="block text-sm font-medium text-gray-700 mb-2">
              Î©îÎ™®
            </label>
            <textarea
              id="description"
              name="description"
              value={form.description}
              onChange={handleInputChange}
              required
              rows={4}
              className="w-full px-4 py-2 rounded-lg border border-gray-300 focus:border-[#4CAF50] focus:ring-1 focus:ring-[#4CAF50] focus:outline-none resize-none"
              placeholder="ÏãùÏû¨Î£åÏóê ÎåÄÌïú Î©îÎ™®Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
            />
          </div>
          <div className="flex gap-4">
            <button
              type="button"
              onClick={() => navigate('/')}
              className="flex-1 px-4 py-2 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50"
            >
              Ï∑®ÏÜå
            </button>
            <button
              type="submit"
              className="flex-1 px-4 py-2 bg-[#4CAF50] text-white rounded-lg hover:bg-[#45a049]"
            >
              {editingProduct ? 'ÏàòÏ†ïÌïòÍ∏∞' : 'Îì±Î°ùÌïòÍ∏∞'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};
