import React, { useState } from 'react';
import { Link } from 'react-router-dom';

interface Product {
  id: number;
  name: string;
  price: number;
  image: string;
  rating: number;
}

const initialProducts: Product[] = [
  { id: 1, name: '요플렛', price: 4900, image: '/images/onion.jpg', rating: 4.5 },
  { id: 2, name: '부대찌개 밀키트', price: 7900, image: '/images/potato.jpg', rating: 4.8 },
  { id: 3, name: '우유 1L', price: 2300, image: '/images/milk.jpg', rating: 4.2 },
  { id: 4, name: '계란 10구', price: 3500, image: '/images/egg.jpg', rating: 4.7 }
];

const categories = ['전체', '채소', '과일', '곡물', '견과류', '버섯', '양념채소'];

export const HomePage: React.FC = () => {
  const [products, setProducts] = useState<Product[]>(initialProducts);

  const handleDelete = (id: number) => {
    setProducts(prev => prev.filter(product => product.id !== id));
  };

  return (
    <div className="min-h-screen bg-[#F8FAF7]">
      {/* 네비게이션 바 */}
      <nav className="bg-white shadow-sm">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center">
              <Link to="/" className="text-2xl font-bold text-[#4CAF50]">
                COOKFARM
              </Link>
            </div>
            <div className="flex-1 max-w-2xl mx-4">
              <div className="relative">
                <input
                  type="text"
                  placeholder="식재료를 입력하세요"
                  className="w-full px-4 py-2 rounded-lg border border-gray-300 focus:border-[#4CAF50] focus:ring-1 focus:ring-[#4CAF50] focus:outline-none"
                />
                <button className="absolute right-3 top-2.5 text-gray-400 hover:text-[#4CAF50]">
                  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                  </svg>
                </button>
              </div>
            </div>
            <div className="flex items-center">
              <Link
                to="/login"
                className="inline-flex items-center px-4 py-2 border border-[#4CAF50] text-sm font-medium rounded-md text-[#4CAF50] hover:bg-[#4CAF50] hover:text-white transition-colors"
              >
                로그인
              </Link>
            </div>
          </div>
        </div>
      </nav>

      {/* 메인 컨텐츠 */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* 카테고리 */}
        <div className="mb-8">
          <div className="flex space-x-4 overflow-x-auto pb-4">
            {categories.map((category, index) => (
              <button
                key={index}
                className={`px-4 py-2 rounded-full text-sm font-medium whitespace-nowrap
                  ${index === 0
                    ? 'bg-[#4CAF50] text-white'
                    : 'bg-white text-gray-600 hover:bg-[#E8F5E9] hover:text-[#4CAF50]'
                  }`}
              >
                {category}
              </button>
            ))}
          </div>
        </div>

        {/* 상품 카드 */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
          {products.map(product => (
            <div
              key={product.id}
              className="bg-[rgb(118,188,130)] rounded-lg shadow-sm hover:shadow-md transition-shadow p-4"
            >
              <h3 className="text-lg font-semibold text-white mb-4">{product.name}</h3>
              <div className="flex justify-end">
                <button
                  onClick={() => handleDelete(product.id)}
                  className="px-3 py-1 bg-white text-[rgb(118,188,130)] font-semibold rounded-lg text-sm hover:bg-gray-100"
                >
                  삭제
                </button>
              </div>
            </div>
          ))}
        </div>
      </main>
    </div>
  );
};
