import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';

interface Product {
  id: number;
  name: string;
  category: string;
  dateAdded: string;
  expiryDate: string;
  description: string;
}

const categories = ['전체', '채소', '과일', '육류', '유제품'];

export const HomePage: React.FC = () => {
  const [products, setProducts] = useState<Product[]>([]);
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const stored = localStorage.getItem('products');
    if (stored) {
      setProducts(JSON.parse(stored));
    }
  }, []);

  const handleDelete = (id: number) => {
    const updated = products.filter(product => product.id !== id);
    setProducts(updated);
    localStorage.setItem('products', JSON.stringify(updated));
  };

  const closeSidebar = () => setSidebarOpen(false);

  return (
    <div className="min-h-screen bg-[#F8FAF7] relative">
      {sidebarOpen && (
        <div
          className="fixed inset-0 bg-black bg-opacity-25 z-40"
          onClick={closeSidebar}
        ></div>
      )}

      {/* 사이드바 */}
      <div className={`fixed top-0 left-0 h-full w-64 bg-white shadow-lg z-50 transform ${sidebarOpen ? 'translate-x-0' : '-translate-x-full'} transition-transform duration-300 ease-in-out`}>
        <div className="p-6">
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-xl font-bold text-[#4CAF50]">COOKFARM</h2>
            <button
              onClick={closeSidebar}
              className="text-gray-500 hover:text-[#4CAF50]"
              aria-label="사이드바 닫기"
            >
              <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
          </div>
          <ul className="space-y-4">
            <li>
              <button onClick={() => { navigate('/add'); closeSidebar(); }} className="text-gray-700 hover:text-[#4CAF50] font-medium">
                식재료 추가
              </button>
            </li>
            <li>
              <button className="text-gray-700 hover:text-[#4CAF50] font-medium">
                식재료 찾기
              </button>
            </li>
            <li>
              <button className="text-gray-700 hover:text-[#4CAF50] font-medium">
                유통기한 달력보기
              </button>
            </li>
          </ul>
        </div>
      </div>

      {/* 네비게이션 바 */}
      <nav className="bg-white shadow-sm relative z-30">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center space-x-4">
              <button onClick={() => setSidebarOpen(true)} className="text-[#4CAF50] hover:text-[#45a049] focus:outline-none">
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16M4 18h16" />
                </svg>
              </button>
              <Link to="/" className="text-2xl font-bold text-[#4CAF50]">
                COOKFARM
              </Link>
            </div>
            <div className="flex-1 max-w-2xl mx-4">
              <div className="relative">
                <input
                  type="text"
                  placeholder="식재료를 입력하세요"
                  className="w-full px-4 py-2 rounded-lg border border-gray-300 focus:border-[#4CAF50] focus:ring-1 focus:ring-[#4CAF50] focus:outline-none"
                />
                <button className="absolute right-3 top-2.5 text-gray-400 hover:text-[#4CAF50]">
                  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                  </svg>
                </button>
              </div>
            </div>
            <div className="flex items-center">
              <Link
                to="/login"
                className="inline-flex items-center px-4 py-2 border border-[#4CAF50] text-sm font-medium rounded-md text-[#4CAF50] hover:bg-[#4CAF50] hover:text-white transition-colors"
              >
                로그인
              </Link>
            </div>
          </div>
        </div>
      </nav>

      {/* 메인 컨텐츠 */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* 카테고리 */}
        <div className="mb-8">
          <div className="flex space-x-4 overflow-x-auto pb-4">
            {categories.map((category, index) => (
              <button
                key={index}
                className={`px-4 py-2 rounded-full text-sm font-medium whitespace-nowrap
                  ${index === 0
                    ? 'bg-[#4CAF50] text-white'
                    : 'bg-white text-gray-600 hover:bg-[#E8F5E9] hover:text-[#4CAF50]'
                  }`}
              >
                {category}
              </button>
            ))}
          </div>
        </div>

        {/* 상품 카드 */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
          {products.map(product => (
            <div
              key={product.id}
              className="bg-[rgb(118,188,130)] rounded-lg shadow-sm hover:shadow-md transition-shadow p-4"
            >
              <h3 className="text-lg font-semibold text-white mb-1">{product.name}</h3>
              <p className="text-sm text-white">유통기한: {product.expiryDate}</p>
              <div className="flex justify-end mt-2">
                <button
                  onClick={() => handleDelete(product.id)}
                  className="px-3 py-1 bg-white text-[rgb(118,188,130)] font-semibold rounded-lg text-sm hover:bg-gray-100"
                >
                  삭제
                </button>
              </div>
            </div>
          ))}
        </div>
      </main>

      {/* + 버튼 */}
      <button
        onClick={() => navigate('/add')}
        className="fixed bottom-8 right-8 w-14 h-14 rounded-full bg-[#4CAF50] text-white text-3xl font-bold shadow-lg flex items-center justify-center hover:bg-[#45a049] transition-colors z-20"
        aria-label="식재료 추가"
      >
        +
      </button>
    </div>
  );
};
